import React, { useState } from 'react'
import Card from './Card'
import "./Cards.css"

const Cards = () => {
  const [items,setItems]=useState([
    "./img/Banana.jpg"
    // {id:1,img:'/img/Banana.jpg',stat:""},
    // {id:1,img:'/img/Banana.jpg',stat:""},
    // {id:2,img:'/img/Cherry.jpg',stat:""},
    // {id:2,img:'/img/Cherry.jpg',stat:""},
    // {id:3,img:'/img/g-apple.jpg',stat:""},
    // {id:3,img:'/img/g-apple.jpg',stat:""},
    // {id:4,img:'/img/Lemon.jpg',stat:""},
    // {id:4,img:'/img/Lemon.jpg',stat:""},
    // {id:5,img:'/img/Orange.jpg',stat:""},
    // {id:5,img:'/img/Orange.jpg',stat:""},
    // {id:6,img:'/img/Pineapple.jpg',stat:""},
    // {id:6,img:'/img/Pineapple.jpg',stat:""},
    // {id:7,img:'/img/Raspberry.jpg',stat:""},
    // {id:7,img:'/img/Raspberry.jpg',stat:""},
    // {id:8,img:'/img/Strawberry.jpg',stat:""},
    // {id:8,img:'/img/Strawberry.jpg',stat:""}
  ])
  const [prev, setPrev] = useState(-1)

    function check(current){
        if(items[current].id == items[prev].id){
            items[current].stat = "correct"
            items[prev].stat = "correct"
            setItems([...items])
            setPrev(-1)
        }else{
            items[current].stat = "wrong"
            items[prev].stat = "wrong"
            setItems([...items])
            setTimeout(() => {
                items[current].stat = ""
                items[prev].stat = ""
                setItems([...items])
                setPrev(-1)
            }, 1000)
        }
    }

    function handleClick(id){
        if(prev === -1){
            items[id].stat = "active"
            setItems([...items])
            setPrev(id)
        }else{
            check(id)
        }
    }

    return (
        <div className="container">
            { items.map((item, index) => (
                <Card key={index} item={item} id={index} handleClick={handleClick} />
            )) }
        </div>
    )
}


export default Cards
